mapper.update:
UPDATE targetai.PKG
SET
	DRL_NM = #{drlNm}
WHERE
	PKG_ID = #{PKG_ID}

mapper.select:
SELECT 
    COUNT(*)
FROM
    targetai.PKG P, targetai.RULE R
WHERE
	P.PKG_ID = R.PKG_ID
	AND P.PKG_ID = #{pkgId}
    AND R.RULE_NM = #{ruleNm}
    AND R.RULE_ID != #{ruleId}

mapper.delete:
DELETE FROM targetai.RULE
WHERE RULE_ID IN 

	#{ruleId}

DELETE FROM targetai.RULE_ATTR
WHERE RULE_ID IN 

	#{ruleId}

mapper.update:
UPDATE targetai.RULE
SET
	ATTR_THEN = #{ATTR_THEN}
WHERE
	RULE_ID = #{ruleId}

UPDATE targetai.PKG
SET
	DRL_SOURCE = #{DRL_SOURCE}
WHERE
	PKG_ID = #{PKG_ID}
