mapper.select:
	SELECT 
		COUNT(*) AS CNT
	FROM targetai.RULE
	WHERE 1=1
	AND PKG_ID = #{pkgId}
	AND RULE_ID = #{ruleId_search}
    AND REG_USRID = #{ruleRegUsrId_search}
    AND RULE_NM LIKE CONCAT('%', #{ruleNm_search}, '%')

SELECT 
    COUNT(*)
FROM
    targetai.PKG P, targetai.RULE R
WHERE
	P.PKG_ID = R.PKG_ID
	AND P.PKG_ID = #{pkgId}
    AND R.RULE_NM = #{ruleNm}
    AND R.RULE_ID != #{ruleId}
