mapper.select:
SELECT FACTOR_ID, PID, FACTOR_NM, FACTOR_NM_EN, FACTOR_TYPE FROM targetai.FACTOR WHERE ATTR_USE_YN = 'Y'
AND FACTOR_NM != '함수' AND FACTOR_TYPE != #{notIn}
AND (FACTOR_TYPE = 'GROUP' OR FACTOR_TYPE = 'DATA' AND DATA_TYPE = #{dataType});

mapper.update:
UPDATE targetai.RULE
SET
	RULE_THEN = #{RULE_THEN}
WHERE
	RULE_ID = #{ruleId}

mapper.delete:
DELETE FROM targetai.RULE
WHERE RULE_ID IN 

	#{ruleId}

DELETE FROM targetai.PKG_RULE_MAPPING
WHERE RULE_ID IN 

	#{ruleId}
