mapper.select:
SELECT 
	COUNT(*) AS CNT
FROM targetai.SVC
WHERE 1=1
AND SVC_ID = #{svcId_search}
AND SVC_NM LIKE CONCAT('%', #{svcNm_search}, '%')
AND SVC_ACT_YN = #{svcActYn_search}
AND REG_USRID = #{svcRegUsrId_search}

mapper.insert:
INSERT INTO targetai.SVC_OUTPUT_VALUE (
	SVC_ID,
	FACTOR_ID
) VALUES 

(
	#{svcId},
	#{opv.factorId}
)
